import React from 'react';
import './Createpost.css';

export default function Createpost() {
    const loadfile = (event)=>{
        var output = document.getElementById('output');
    output.src = URL.createObjectURL(event.target.files[0]);
    output.onload = function() {
      URL.revokeObjectURL(output.src)
    }};
  return (
    <div className='createPost'>
        <div className='post-header'>
            <h4 style={{margin:"3px auto"}}>Create New Post</h4>
            <button id='post-btn'>Share</button>
        </div>
        <div className='main-div'>
        <img src='' alt='' id='output'/>
            <input type='file' accept='image/*' onChange={(event)=>{
                loadfile(event)}}></input>
        </div>
        <div className='details'>
            <div className='card-header'>
                <div className='card-pic'>
                    <img src='https://plus.unsplash.com/premium_photo-1668116307088-583ee0d4aaf7?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHx0b3BpYy1mZWVkfDIzfGJvOGpRS1RhRTBZfHxlbnwwfHx8fHw%3D&auto=format&fit=crop&w=500&q=60' alt=''></img>
                </div>
                <h5>Ramesh</h5>
            </div>
            <textarea type='text' placeholder="Write a caption"></textarea><br/>
        </div>
    </div>
  )
}
