import React, {useState} from 'react';
import logo from '../img/logo.webp';
import './SignUp.css';
import { Link,useNavigate } from "react-router-dom";

import { toast } from 'react-toastify';

export default function SignUp() {
  const navigate=useNavigate();
  const [name,setName]= useState("");
  const [email,setEmail]=  useState("");
  const [userName,setUserName]= useState("");  
  const [password,setPassword]= useState("");
    

  const notifyA = (msg)=> toast.error(msg)
  const notifyB = (msg)=> toast.success(msg)
   
  // const emailRegex =/^([A-Za-z0-9_\-.])+@([A-Za-z0-9_\-.])+.([A-Za-z]{2,4})$/;
  // const passRegex =/^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{7,15}$/;

  const postData = ()=>{
    //checking email
    // if(!emailRegex.test(email)){
    //   notifyA("Invalid email")
    //   return 
    // } 
    // else if(!passRegex.test(password)){
    //   notifyA("Password must contain at least 8 characters including at least one number and one special character")
    //   return
    // }

    //sending to the server
    fetch("http://localhost:5000/signup",{
      method:'post',
      headers:{
        "Content-Type":"application/json"
      },
      body:JSON.stringify({
        name:name,
        userName:userName,
        email:email,
        password:password
      })
    }).then(res=>res.json())
    .then(data=>{
      if(data.error){
        notifyA(data.error)
      }
      else{
        notifyB(data.message)
        navigate("/signin")
      }
      console.log(data)})
  }
  return (
    <div className='signUp'>
      <div className='form-container'>
        <div className='form'>
         <img  className='signUpLogo' src={logo} alt=''/>

          <p className='loginPara'>
            Sign up to see photos and videos<br/> from your friends
          </p>
          <div>
            <input type='email' name='email' id='email' value={email} placeholder='Email' 
            onChange={(e)=>{setEmail(e.target.value)}}/>
          </div>

          <div>
            <input type='text' name='name' id='name' value={name} 
            onChange={((e)=>{setName(e.target.value) })} placeholder='Full Name'/>
          </div>

          <div>
            <input type='text' name='username' id='username' placeholder='Username' value={userName} 
            onChange={(e)=>{setUserName(e.target.value)
            }}/>
          </div>

          <div>
            <input type='password' name='password' id='password' value={password} onChange={(e)=>{
              setPassword(e.target.value)
            }} placeholder='Password'/>
          </div>

          <div>
            <p className='loginPara' style={{fontSize:"11px",margin:"3px"}}>
              By signing up, you agree to out Terms, <br/>privacy policy and cookies policy.
            </p>
            <input type='submit' id='submit-btn' value="Sign Up" onClick={()=>{
              postData()
            }}/> 
          </div>

        </div>  

        <div className='Form2'>
          Already have an account ?
          <Link to="/signin">
          <span style={{color:"blue",cursor:"pointer"}}>Sign In</span>
          </Link>
          
        </div>
      </div>
    </div>
  )
};
